<?php/** *@package Anta */ /** * Term frequency – inverse document frequency and other metrics applied to each entity * Term frequency (tf) measures a word's relevancy in a single text.  * Document frequency (df) measures a word's overall relevancy across documents.  * Dividing tf by df yields tf-idf, a simple and elegant measurement of a word's uniqueness  * in a text when compared to other texts. For example, this can be used in a search engine to rank documents given a user query. */class Application_Model_EntityMetrics{		/** 	 * @var array of document id => term frequencies	 */	public $ds = array();		public $t;		public function __construct( $label ){		$this->t = $label;	}		/**	 * return the max tf-idf measure of uniqueness	 */	public function getMaxUniqueness( $amountOfDocuments ){		$df = count( $this->ds ) / $amountOfDocuments;		$max = 0;		foreach( $this->ds as $d=>$tf ){			$max = max( 				$max,				$tf * log( 1 / $df )			);		}		return $max;	}		public function getNumberOfDocuments(){		return count($this->ds);	}		/**	 * append the term frequency related to the document specified	 * @param string idDocument	- the document specified	 * @param float termFrequency	- term frequency value	 */	public function appendTermFrequency( $idDocument, $termFrequency){		$this->ds[$idDocument] = $termFrequency;	}	}