<?php /**  * @package Forms_Validators  */  /**   * Validate email entries  */ class Application_Model_Forms_Validators_EmailValidator extends Application_Model_Forms_Validators_JsValidator {    	const LENGTH = 'length';    const UPPER  = 'upper';    const LOWER  = 'lower';    const DIGIT  = 'digit';    			protected $_messageTemplates = array(				self::UPPER  => "'*******' must contain at least one uppercase letter",		self::LOWER  => "'*******' must contain at least one lowercase letter",		self::DIGIT  => "'*******' must contain at least one digit character"    );    			public function isValid($value){		$this->_setValue($value);				if (strlen($value) < $this->minLength ) {			$this->_error( "'".$value."' must be at least " .$this->minLength. " characters in length" );						return false;		}        		if (!preg_match('/^[a-zA-Z][\w\.-]*[a-zA-Z0-9]@[a-zA-Z0-9][\w\.-]*[a-zA-Z0-9]\.[a-zA-Z][a-zA-Z\.]*[a-zA-Z]$/', $value)) {			$this->_error( "'".$value."' seems not to be a valid email address"  );			return false;		}        return true;	}		}?>