<?php /**  * @package Forms_Validators  */  /**   * Validate options in a select stuff. This is a Special validato that allow to validate between a range of possibles values.  * It is not restricted to the select html input!  *  * <code>  * usage new Application_Model_Forms_Validators_SelectValidator( array( "validOptions"=> array("valid", "valid2"...)) );  * </code>  */ class Application_Model_Forms_Validators_SelectValidator extends Application_Model_Forms_Validators_JsValidator {    	public $validOptions = array();		/**	 * add a valid value to validate select entry. Use this with every option you want to be validate	 * @param $value;	 */	public function addValidValue( $value ){		$this->validOptions[] = $value;	}		public function isValid($value){		$this->_setValue($value);				if( in_array( $value, $this->validOptions ) ) return true;				$this->_error("please chose a value from the given list");		return false;	}		}