<?php/** * describe a graph entry in graphs table */class Application_Model_Graph{	public $id;	public $cryptoId;		public $description = "";	public $date;	public $localUrl ="";		public $status = "";	public $error = "";		public function __construct( $id, $engine, $date="", $description="", $localUrl="", $status="", $error="" ){		$this->id = $id;		$this->cryptoId = Dnst_Crypto_SillyCipher::crypt( $id );				$this->description = $description;		$this->date = $date;		$this->localUrl = $localUrl;				$this->status = $status;		$this->error = $error;	}		}?>