<?phpclass Application_Model_Ngr_EntitiesTagsMapper{	public static function install( $username ){		$stmt = Anta_Core::mysqli()->query( 			"CREATE TABLE IF NOT EXISTS anta_".$username.".`ngr_entities_tags` (				`id_ngr_entity` int(11) NOT NULL,				`id_tag` int(11) NOT NULL,				UNIQUE KEY `id_ngr_entity` (`id_ngr_entity`,`id_tag`),				FOREIGN KEY ( `id_ngr_entity` ) REFERENCES anta_".$username.".`ngr_entities`( id_ngr_entity )                      ON DELETE CASCADE,				FOREIGN KEY ( `id_tag` ) REFERENCES anta_".$username.".`tags`( id_tag )                      ON DELETE CASCADE	  			) ENGINE=INNODB"        );	}		public static function add( Application_Model_User $antaUser, $idEntity, $idTag ){				$stmt = Anta_Core::mysqli()->query( 			"INSERT IGNORE INTO anta_".$antaUser->username.".`ngr_entities_tags` (				`id_ngr_entity`, `id_tag` 			) VALUES (				?, ?			)", array( $idEntity, $idTag )		);				return $stmt->rowCount();	}		public static function remove( Application_Model_User $antaUser, $idEntity, $idTag ){				$stmt = Anta_Core::mysqli()->query( 			"DELETE FROM anta_".$antaUser->username.".`ngr_entities_tags` WHERE id_ngr_entity = ? AND id_tag = ?",			array(				$idEntity, $idTag			)		);				return $stmt->rowCount();	}	}?>