<?phpclass Application_Model_Rws_EntitiesTagsMapper{	public static function install( $username ){		$stmt = Anta_Core::mysqli()->query( 			"CREATE TABLE IF NOT EXISTS anta_".$username.".`rws_entities_tags` (				`id_rws_entity` int(11) NOT NULL,				`id_tag` int(11) NOT NULL,				UNIQUE KEY `id_rws_entity` (`id_rws_entity`,`id_tag`),				FOREIGN KEY ( `id_rws_entity` ) REFERENCES anta_".$username.".`rws_entities`( id_rws_entity )                      ON DELETE CASCADE,				FOREIGN KEY ( `id_tag` ) REFERENCES anta_".$username.".`tags`( id_tag )                      ON DELETE CASCADE	  			) ENGINE=INNODB"        );	}		public static function add( Application_Model_User $antaUser, $idEntity, $idTag ){				$stmt = Anta_Core::mysqli()->query( 			"INSERT IGNORE INTO anta_".$antaUser->username.".`rws_entities_tags` (				`id_rws_entity`, `id_tag` 			) VALUES (				?, ?			)", array( $idEntity, $idTag )		);				return $stmt->rowCount();	}		public static function remove( Application_Model_User $antaUser, $idEntity, $idTag ){				$stmt = Anta_Core::mysqli()->query( 			"DELETE FROM anta_".$antaUser->username.".`rws_entities_tags` WHERE id_rws_entity = ? AND id_tag = ?",			array(				$idEntity, $idTag			)		);				return $stmt->rowCount();	}	}?>