<?php/** * @package Ui_Crafts_Items */ /** * describe an html object, a generic item to be used in a FlowModule * */ class Ui_Crafts_Items_Category extends Ui_Crafts_Item {	/**	 * a category object	 * @var Application_Model_Category	 */	public $category;		/**	 * an array of Application_Model_Tag tags 	 * @var array	 */	public $tags;		/**	 * Class constructor	 */	public function __construct( Application_Model_Category $category, $user ){				$this->category = $category;		$this->user =& $user;		$this->tags = array();				parent::__construct( $category->content );	}		public function addTag( Application_Model_Tag $tag ){		$this->tags[] = $tag;	}		public function __toString(){		$content = "";				foreach( array_keys( $this->tags ) as $k ){			$tag =& $this->tags[ $k ];			$content .= '			   <li class="is-tag">			     <a href="'.Anta_Core::getBase().'/tags/whatis/id/'.$tag->cryptoId.'/user/'.$this->user->cryptoId.'">'.$this->category->content.':<strong>'.$tag->content.'</strong></a>			     <a class="remove-document-tag" href="tag='.$tag->cryptoId.'"><img src="'.Anta_Core::getBase().'/images/cross-small.png" /></a>			   </li>			';		}						return '			<div class="grid_22 prefix_1 alpha omega item-category">				<ul class="listed-tags" id="cat_'.$this->category->content.'">'.$content.'</ul>			</div>';			}	 }