<?php/** * @package Ui_Crafts_Items */ /** * Use it to obtain a free html text container, using auto id * */ class Ui_Crafts_Items_Text extends Ui_Crafts_Item {		static $_i_d = 0;			public function __construct( $content ){		parent::__construct( self::$_i_d++ );		$this->content = $content;	}		public function __toString(){		return '<div class="grid_22 prefix_1 suffix_1 alpha omega margin_1">'.$this->content.'</div>';	} } ?>