<?php/** * @package Ui_Forms *//** * Allow to connect to a google doc already prepared. * A google doc can be created from the given csv file (function export tags for all) * The csv parsing is done by Anta_Csv::parse( $filepath ) */ class Ui_Forms_ImportGoogleDocs extends Ui_Form{		protected function _init(){		        $content = new Ui_Forms_Elements_Input( 'text', I18n_Json::get( "google document address url" ), array(			"name"  => "url-gdoc",			"id"    => "url-gdoc",			"class" => "width_7 margin_1"		));			$submit = new Ui_Forms_Elements_Input( "button", $this->title, array(			"name"  => "load-gdoc",			"id"    => "load-gdoc",			"value" => $this->title		));				$this->addElement( $content );		$this->addElement( $submit );	}		public function __toString(){		$this->content = '			<div class="grid_22 prefix_1 suffix_1 alpha omega">				<p>'.$this->url_gdoc->label.'</p>				'.$this->url_gdoc.'&nbsp;				'.$this->load_gdoc.'			</div>';				return parent::__toString();	}}?>