<?php/** * @package Ui_Forms *//** * create and describe a form with a search text field and a button */ class Ui_Forms_Search extends Ui_Form{	protected function _init(){	        $query = new Application_Model_Forms_Elements_Input( 'text', I18n_Json::get( "search" ), array(			"name"  => "query",			"id"    => "query",			"class" => "width_7 height_2 margin_1 "		));				$submit = new Application_Model_Forms_Elements_Input( "submit", $this->title, array(			"name"  => "send-fields",			"id"    => "send-fields",						"value" => I18n_Json::get( "find" )		));				/** add created element */		$this->addElement( $query );		$this->addElement( $submit );	}		public function __toString(){		$this->content = '		<div class="grid_24 alpha omega centered">			<div class="grid_24 alpha omega">'.$this->query.' '.$this->send_fields.'</div>		</div>';		return parent::__toString();	}}		