<?php /**  * @package Ui_Forms_Validators  */  /**   * Validate date format entries  * dd/mm/yyyy  *  */ class Ui_Forms_Validators_Date extends Ui_Forms_Validator {    		public function isValid($value){		$this->_setValue($value);				if ( empty($value) && $this->minLength == 0 ){			// optional value			return true;		}		if (strlen($value) < $this->minLength || strlen($value) > $this->maxLength) {			$this->_error( "'".$value."' must be exactly " .$this->maxLength. " characters in length" );			return false;		}		if (!preg_match( '/(\d{2})\/(\d{2})\/(\d{4})/', $value, $matches ) ) {						$this->_error( "'".$value."' format must be dd/mm/yyyy, e.g 24/08/1987" );			return false;		}				$year  =& $matches[3];		$month =& $matches[2];				if( $year < 1400 ) {			$this->_error( "'".$value."' <b>year</b> must be after 2010" );			return false;		}				if( $month > 12 || $month < 1 ) {			$this->_error( "'".$value."' <b>month</b> is not valid. values between 01-12" );			return false;		}		        return true;	}		}