<?php/** *@package Ui_Forms_Validators */ /** * Validate an array of string using the given validator */ class Ui_Forms_Validators_Iterator extends Ui_Forms_Validator{		public $validator;		public function isValid( $values ){				foreach( $values as $value ){			if( $this->validator->isValid( $value ) === false ) return false;		}		return true;	}		public function getMessages(){		return $this->validator->getMessages();	}}?>